import { Header, Succes, Failed } from "../components"
import { UserContext } from "../context/UserContext"
import { useContext, useState } from "react"
import { useHistory } from 'react-router-dom';



const LoginPage = () => {
  const {setLogin, setUsername, login} = useContext(UserContext)
  const [succes, setSucces] = useState(false)
  const [failed, setFailed] = useState(false)

  const [name, setName] = useState("")
  const [password, setPassword] = useState("")

  useEffect(() => {
    if (succes || failed) {
      const timer = setTimeout(() => {
        setSuccess(false);
        setFailed(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, []);

  function logging(e){
    e.preventDefault()

    const data={
      name :name,
      password:password
    }

    fetch ("http://localhost:4000/login",{
      method:"POST",
      headers :{
        "Content-Type" : "application/json"
      },
      body: JSON.stringify(data)
    })
    .then(response =>{
      if(response.ok){
        return response.json()
      }else{
        throw new Error("something went wrong")
      }
    })

    .then(data =>{
      if(data === "failed"){
        setFailed(true)
      }else{
      console.log(data.token, data.authorized, data.username)
      localStorage.setItem("token", data.token)
      setLogin(data.authorized)
      setUsername(data.username)

      setSucces(true)
      }
    })
  }

  function clickbut(){
    setLogin(!login)
  }


  return (
    <main>
        <Header/>
            
            <form onSubmit={logging}>
                <h1>Log<span>In</span></h1>
                <input 
                  type="text"  
                  placeholder="Name"
                  value={name}
                  onChange={e => setName(e.target.value)}
                  pattern=".{0, 6}" 
                  title="Input should not exceed 6 characters"/><br />
                <input 
                  type="password"  
                  placeholder="Password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}/><br />
                <button>Submit</button>
            </form>

            <button onClick={clickbut}>change login</button>
            {login ? <div>login true</div> : <div>login false</div>} 

            
    </main>
  )
}
export default LoginPage




