import './styles/App.css';
import './styles/LoginPage.css' 
import { Route, Routes, Link } from 'react-router-dom';
import { IndexPage, RegisterPage, LoginPage } from './Pages';
import { UserContext } from './context/UserContext';
import { useState, useEffect } from 'react';








function App() {

  function getTokenFromLocalStorage() {
    try{
    return localStorage.getItem('token');
  } catch(error){
    console.log(error)
    return null
  }
  }

  function validateToken(){
    if (token){
    fetch("http://localhost:4000/validateToken", {
      method: "POST",
      headers: {
        authorization: token
      }
    })
    .then(response =>{
      if(response.ok){
        return response.json()
      }else{
        throw new Error("token validation failed")
      }
    })
    .then(data =>{
      setLogin(data.authorization)
      setUsername(data.username)
    })
  }
}

  const token=getTokenFromLocalStorage()

  useEffect(() => {
    validateToken();
  }, []);
  //to use in file import usecontext and Usercontext from ./context, infile  const {variable} = useContext(UserContext)
  const [login, setLogin] = useState(false)
  const [username, setUsername] = useState('')

  return (
    <UserContext.Provider value={{login, setLogin, username,setUsername}}>
      <Routes>
        <Route index element={<IndexPage/>}/>
        <Route path="/Register" element={<RegisterPage/>}/>
        <Route path="/Login" element={<LoginPage/>}/>
      </Routes>
    </UserContext.Provider>


  );
}

export default App;
