import { Header } from "../components"
import { UserContext } from "../context/UserContext"
import { useContext, useState } from "react"


const LoginPage = () => {
  const {login, setLogin, setUsername} = useContext(UserContext)


  const [name, setName] = useState("")
  const [password, setPassword] = useState("")

  function logging(e){
    e.preventDefault()

    const data={
      name :name,
      password:password
    }

    const response = fetch ("http://localhost:4000/login",{
      method:"POST",
      headers :{
        "Content-Type" : "application/json"
      },
      body: JSON.stringify(data)
    })
    .then(response =>{
      if(response.ok){
        console.log("data recived by server")
      }else{
        throw new Error("something went wrong")
      }
    })

    .then(data =>{
      console.log("response is:" + data)
      if(data.authenticted){
        setLogin(true)
        setUsername(data.user)
        window.location.href = '/'
      } else{
        //display error
      }
    })
    .catch(error =>{
      console.log(error)

    })
  }


  return (
    <main>
        <Header/>
            
            <form onSubmit={logging}>
                <h1>Log<span>In</span></h1>
                <input 
                  type="text"  
                  placeholder="Name"
                  value={name}
                  onChange={e => setName(e.target.value)}
                  pattern=".{0, 6}" 
                  title="Input should not exceed 6 characters"/><br />
                <input 
                  type="password"  
                  placeholder="Password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}/><br />
                <button>Submit</button>
            </form>

            
    </main>
  )
}
export default LoginPage




